// client/src/components/ErrorMessage.js
import React from 'react';
import '../styles/components/ErrorMessage.css';

const ErrorMessage = ({ error, suggestions = [], onSuggestionClick }) => {
  const defaultSuggestions = [
    "Double-check the spelling of the medication",
    "Try searching for the generic name instead",
    "Try a more common over-the-counter medication"
  ];

  const displaySuggestions = suggestions.length > 0 ? suggestions : defaultSuggestions;

  return (
    <div className="error-container">
      <div className="error-icon">
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M12 22C6.477 22 2 17.523 2 12C2 6.477 6.477 2 12 2C17.523 2 22 6.477 22 12C22 17.523 17.523 22 12 22ZM12 20C16.418 20 20 16.418 20 12C20 7.582 16.418 4 12 4C7.582 4 4 7.582 4 12C4 16.418 7.582 20 12 20ZM11 15H13V17H11V15ZM11 7H13V13H11V7Z" fill="currentColor"/>
        </svg>
      </div>
      <div className="error-content">
        <h4 className="error-title">Couldn't find that medication</h4>
        <p className="error-message">{error || "I couldn't find information about that medication in our database."}</p>

        <div className="error-suggestions">
          <p className="suggestions-title">Try these suggestions:</p>
          <ul className="suggestions-list">
            {displaySuggestions.map((suggestion, index) => (
              <li key={index} className="suggestion-item">
                {onSuggestionClick ? (
                  <button 
                    className="suggestion-button" 
                    onClick={() => onSuggestionClick(suggestion)}
                  >
                    {suggestion}
                  </button>
                ) : (
                  suggestion
                )}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ErrorMessage;
